/* eslint-disable no-use-before-define */
/* eslint-disable react/prop-types */
import React, { useEffect } from "react";
import { useLocation, matchPath } from "react-router";
import { Link as RouterLink } from "react-router-dom";
import { useSelector } from "react-redux";
import PerfectScrollbar from "react-perfect-scrollbar";
import PropTypes from "prop-types";
import { Avatar, Box, Chip, Divider, Drawer, Hidden, Link, List, ListSubheader, Typography, makeStyles } from "@material-ui/core";
import ReceiptIcon from "@material-ui/icons/ReceiptOutlined";
import {
	Briefcase as BriefcaseIcon,
	Calendar as CalendarIcon,
	ShoppingCart as ShoppingCartIcon,
	Folder as FolderIcon,
	BarChart as BarChartIcon,
	Lock as LockIcon,
	UserPlus as UserPlusIcon,
	Shield as ShieldIcon,
	AlertCircle as AlertCircleIcon,
	Trello as TrelloIcon,
	User as UserIcon,
	Layout as LayoutIcon,
	Edit as EditIcon,
	DollarSign as DollarSignIcon,
	Mail as MailIcon,
	MessageCircle as MessageCircleIcon,
	PieChart as PieChartIcon,
	Share2 as ShareIcon,
	Users as UsersIcon,
} from "react-feather";
import Logo from "src/components/Logo";
import NavItem from "./NavItem";

const navConfig = [
	{
		subheader: "Reports",
		items: [
			{
				title: "Dashboard",
				icon: PieChartIcon,
				href: "/app/reports/dashboard",
			},
			{
				title: "Dashboard Alternative",
				icon: BarChartIcon,
				href: "/app/reports/dashboard-alternative",
			},
		],
	},
	{
		subheader: "Management",
		items: [
			{
				title: "Customers",
				icon: UsersIcon,
				href: "/app/management/customers",
				items: [
					{
						title: "List Customers",
						href: "/app/management/customers",
					},
					{
						title: "View Customer",
						href: "/app/management/customers/1",
					},
					{
						title: "Edit Customer",
						href: "/app/management/customers/1/edit",
					},
				],
			},
			{
				title: "Products",
				icon: ShoppingCartIcon,
				href: "/app/management/products",
				items: [
					{
						title: "List Products",
						href: "/app/management/products",
					},
					{
						title: "Create Product",
						href: "/app/management/products/create",
					},
				],
			},
			{
				title: "Orders",
				icon: FolderIcon,
				href: "/app/management/orders",
				items: [
					{
						title: "List Orders",
						href: "/app/management/orders",
					},
					{
						title: "View Order",
						href: "/app/management/orders/1",
					},
				],
			},
			{
				title: "Invoices",
				icon: ReceiptIcon,
				href: "/app/management/invoices",
				items: [
					{
						title: "List Invoices",
						href: "/app/management/invoices",
					},
					{
						title: "View Invoice",
						href: "/app/management/invoices/1",
					},
				],
			},
		],
	},
	{
		subheader: "PHR",
		icon: BriefcaseIcon,
		items: [
			{
				title: "Healthcare Provider Organizations",
				href: "/phr/organizations",
				icon: BriefcaseIcon,
			},
		],
	},
	{
		subheader: "Applications",
		items: [
			{
				title: "Projects Platform",
				href: "/app/projects",
				icon: BriefcaseIcon,
				items: [
					{
						title: "Overview",
						href: "/app/projects/overview",
					},
					{
						title: "Browse Projects",
						href: "/app/projects/browse",
					},
					{
						title: "Create Project",
						href: "/app/projects/create",
					},
					{
						title: "View Project",
						href: "/app/projects/1",
					},
				],
			},
			{
				title: "Social Platform",
				href: "/app/social",
				icon: ShareIcon,
				items: [
					{
						title: "Profile",
						href: "/app/social/profile",
					},
					{
						title: "Feed",
						href: "/app/social/feed",
					},
				],
			},
			{
				title: "Kanban",
				href: "/app/kanban",
				icon: TrelloIcon,
			},
			{
				title: "Mail",
				href: "/app/mail",
				icon: MailIcon,
				info: () => <Chip color="secondary" size="small" label="Updated" />,
			},
			{
				title: "Chat",
				href: "/app/chat",
				icon: MessageCircleIcon,
				info: () => <Chip color="secondary" size="small" label="Updated" />,
			},
			{
				title: "Calendar",
				href: "/app/calendar",
				icon: CalendarIcon,
			},
		],
	},
	{
		subheader: "Auth",
		items: [
			{
				title: "Login",
				href: "/login-unprotected",
				icon: LockIcon,
			},
			{
				title: "Register",
				href: "/register-unprotected",
				icon: UserPlusIcon,
			},
			{
				title: "Login: Guest Protected",
				href: "/login",
				icon: ShieldIcon,
			},
			{
				title: "Register: Guest Protected",
				href: "/register",
				icon: ShieldIcon,
			},
		],
	},
	{
		subheader: "Pages",
		href: "/app/pages",
		items: [
			{
				title: "Account",
				href: "/app/account",
				icon: UserIcon,
			},
			{
				title: "Error",
				href: "/404",
				icon: AlertCircleIcon,
			},
			{
				title: "Pricing",
				href: "/pricing",
				icon: DollarSignIcon,
			},
		],
	},
	{
		subheader: "Extra",
		items: [
			{
				title: "Charts",
				href: "/app/extra/charts",
				icon: BarChartIcon,
				items: [
					{
						title: "Apex Charts",
						href: "/app/extra/charts/apex",
					},
				],
			},
			{
				title: "Forms",
				href: "/app/extra/forms",
				icon: EditIcon,
				items: [
					{
						title: "Formik",
						href: "/app/extra/forms/formik",
					},
					{
						title: "Redux Forms",
						href: "/app/extra/forms/redux",
					},
				],
			},
			{
				title: "Editors",
				href: "/app/extra/editors",
				icon: LayoutIcon,
				items: [
					{
						title: "DraftJS Editor",
						href: "/app/extra/editors/draft-js",
					},
					{
						title: "Quill Editor",
						href: "/app/extra/editors/quill",
					},
				],
			},
		],
	},
];

function renderNavItems({ items, ...rest }) {
	return <List disablePadding>{items.reduce((acc, item) => reduceChildRoutes({ acc, item, ...rest }), [])}</List>;
}

function reduceChildRoutes({ acc, pathname, item, depth = 0 }) {
	const key = item.title + depth;

	if (item.items) {
		const open = matchPath(pathname, {
			path: item.href,
			exact: false,
		});

		acc.push(
			<NavItem depth={depth} icon={item.icon} key={key} info={item.info} open={Boolean(open)} title={item.title}>
				{renderNavItems({
					depth: depth + 1,
					pathname,
					items: item.items,
				})}
			</NavItem>
		);
	} else {
		acc.push(<NavItem depth={depth} href={item.href} icon={item.icon} key={key} info={item.info} title={item.title} />);
	}

	return acc;
}

