import React, { useRef, useState } from "react";
import { Link as RouterLink } from "react-router-dom";
import PropTypes from "prop-types";
import clsx from "clsx";
import { Breadcrumbs, Button, Grid, Link, Menu, MenuItem, SvgIcon, Typography, makeStyles } from "@material-ui/core";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import { Calendar as CalendarIcon } from "react-feather";

const timeRanges = [
	{
		value: "today",
		text: "Today",
	},
	{
		value: "yesterday",
		text: "Yesterday",
	},
	{
		value: "last_30_days",
		text: "Last 30 days",
	},
	{
		value: "last_year",
		text: "Last year",
	},
];

const useStyles = makeStyles((theme) => ({
	root: {},
	actionIcon: {
		marginRight: theme.spacing(1),
	},
}));

function Header({ className, ...rest }) {
	const classes = useStyles();
	const actionRef = useRef(null);
	const [isMenuOpen, setMenuOpen] = useState(false);
	const [timeRange, setTimeRange] = useState(timeRanges[2].text);

	return (
		<Grid className={clsx(classes.root, className)} container justify="space-between" spacing={3} {...rest}>
			<Grid item>
				<Breadcrumbs separator={<NavigateNextIcon fontSize="small" />} aria-label="breadcrumb">
					<Link variant="body1" color="inherit" to="/phr" component={RouterLink}>
						PHR
					</Link>
					<Typography variant="body1" color="textPrimary">
						Dashboard
					</Typography>
				</Breadcrumbs>
				<Typography variant="h3" color="textPrimary">
					Overview
				</Typography>
			</Grid>
			<Grid item>
				<Button ref={actionRef} onClick={() => setMenuOpen(true)}>
					<SvgIcon fontSize="small" className={classes.actionIcon}>
						<CalendarIcon />
					</SvgIcon>
					{timeRange}
				</Button>
				{/* <Menu
          anchorEl={actionRef.current}
          onClose={() => setMenuOpen(false)}
          open={isMenuOpen}
          PaperProps={{ className: classes.menu }}
          getContentAnchorEl={null}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'center'
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'center'
          }}
        >
          {timeRanges.map((t) => (
            <MenuItem
              key={t.value}
              onClick={() => setTimeRange(t.text)}
            >
              {t.text}
            </MenuItem>
          ))}
        </Menu> */}
			</Grid>
		</Grid>
	);
}

Header.propTypes = {
	className: PropTypes.string,
};

Header.defaultProps = {};

export default Header;
