/* eslint-disable arrow-parens */
import React, { useCallback, useState, useEffect } from "react";
import {
	Box,
	Container,
	// Divider,
	// Tab,
	// Tabs,
	makeStyles,
} from "@material-ui/core";
import Page from "src/components/Page";
import CircularProgress from "@material-ui/core/CircularProgress";
import Backdrop from "@material-ui/core/Backdrop";
// import Axios from 'src/utils/axios';
import axios from "axios";
import useIsMountedRef from "src/hooks/useIsMountedRef";
import Header from "./Header";
import Details from "./Details";
// import Invoices from './Invoices';
// import Logs from './Logs';

const useStyles = makeStyles((theme) => ({
	root: {
		backgroundColor: theme.palette.background.dark,
		minHeight: "100%",
		paddingTop: theme.spacing(3),
		paddingBottom: theme.spacing(3),
	},
	backdrop: {
		zIndex: theme.zIndex.drawer + 1,
		color: "#fff",
	},
}));

function DocumentsView({ match }) {
	const classes = useStyles();
	const isMountedRef = useIsMountedRef();

	const [loading, setLoading] = useState(true);
	const [compositions, setCompositions] = useState(null);

	// console.log('composition view index', match);
	// const [currentTab, setCurrentTab] = useState('details');
	// const tabs = [
	//   { value: 'details', label: 'Details' },
	//   { value: 'invoices', label: 'Invoices' },
	//   { value: 'logs', label: 'Logs' }
	// ];

	// const handleTabsChange = (event, value) => {
	//   setCurrentTab(value);
	// };

	const getCompositions = async () => {
		// console.log(2, loading);
		// console.log(match.params.id);
		const url = `http://localhost:4000/4_0_0/Composition?subject=Patient/${match.params.id}`;
		const config = {
			method: "get",
			url,
			// headers: {
			//   Authorization:
			//     'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoiNjAyMjAxZTA1ZjI1MzgwMDE1Zjk4MDU3IiwiaWF0IjoxNjE0MTQxMjE1LCJleHAiOjE2MTY3MzMyMTUsImF1ZCI6Imh0dHBzOi8vbG9jYWxob3N0OjMwMDAiLCJpc3MiOiJzYW1zdW5nIG1lZGljYWwgY2VudGVyIiwic3ViIjoiYWRtaW5Ac21jLmNvbSJ9.1ykOVKtab7g9dUUCg5_tDzR4yYz-TVf3g5Giv4IHJlJXcfF8624yL9KBBnkKoKdsHBtmBO3Eu7BQ4YWSBFHK08NmV3w9Ukub5jSElTuRKggk8_HJeQ4_aqDrHvygwiEDntMiBQujUTKIapWgnz_WfVLJerPe31_xCgco7wAAdtY'
			// }
		};

		await axios(config)
			.then((response) => {
				// console.log(response.data);
				if (isMountedRef.current) {
					setCompositions(response.data);
				}
			})
			.catch((error) => {
				console.log(error);
			});
	};

	useEffect(() => {
		const fetchData = async () => {
			// console.log(1, loading);
			await getCompositions();
			// console.log(3, loading);
			setLoading(false);
			// console.log(4, loading);
		};
		// console.log(0, loading);

		fetchData();
	}, []);

	if (!compositions) {
		return null;
	}
	// console.log(loading);

	return (
		<Page className={classes.root} title="Customer Details">
			<Backdrop className={classes.backdrop} open={loading}>
				<CircularProgress color="inherit" />
			</Backdrop>
			<Container maxWidth={false}>
				<Header compositions={compositions} />

				<Box mt={3}>
					<Details compositions={compositions} />
				</Box>
			</Container>
		</Page>
	);
}

export default DocumentsView;
