/* eslint-disable arrow-parens */
import React from "react";
import { Link as RouterLink } from "react-router-dom";
import PropTypes from "prop-types";
import clsx from "clsx";
import axios from "axios";
import moment from "moment";
import {
	Box,
	Button,
	Card,
	CardHeader,
	Divider,
	Table,
	TableBody,
	TableCell,
	TableRow,
	Typography,
	Dialog,
	Link,
	makeStyles,
	DialogContent,
	DialogContentText,
	DialogTitle,
	DialogActions,
} from "@material-ui/core";
import Label from "src/components/Label";
import DocumentMoreButton from "src/components/DocumentMoreButton";
import ReactJson from "react-json-view";

const useStyles = makeStyles((theme) => ({
	root: {},
	fontWeightMedium: {
		fontWeight: theme.typography.fontWeightMedium,
	},
	actionIcon: {
		marginRight: theme.spacing(1),
	},
}));

function DocumentInfo({ document, className, ...rest }) {
	const classes = useStyles();

	// const title = `Document ID : ${document.id}`;
	// const encounterDate = moment(document.entry[0].resource.date).format("YYYY-MM-DD HH:mm:SS");
	const sections = document.entry[0].resource.section;

	// console.log(0, document);
	const [open, setOpen] = React.useState(false);
	const [resource, setResource] = React.useState();
	const [dialogTitle, setDialogTitle] = React.useState();
	// let url;
	// let resource;

	const handleButtonClick = React.useCallback(async (e) => {
		let resourceInfo = e.target.id;
		setDialogTitle(resourceInfo);
		console.log(1, resourceInfo);
		resourceInfo = resourceInfo.split("/");
		const resourceName = resourceInfo[0];
		const resourceId = resourceInfo[1];
		const url = `http://localhost:4000/4_0_0/${resourceName}/${resourceId}`;

		const getResource = async () => {
			const config = {
				method: "get",
				url,
				// headers: {
				//   Authorization:
				//     'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoiNjAyMjAxZTA1ZjI1MzgwMDE1Zjk4MDU3IiwiaWF0IjoxNjE0MTQxMjE1LCJleHAiOjE2MTY3MzMyMTUsImF1ZCI6Imh0dHBzOi8vbG9jYWxob3N0OjMwMDAiLCJpc3MiOiJzYW1zdW5nIG1lZGljYWwgY2VudGVyIiwic3ViIjoiYWRtaW5Ac21jLmNvbSJ9.1ykOVKtab7g9dUUCg5_tDzR4yYz-TVf3g5Giv4IHJlJXcfF8624yL9KBBnkKoKdsHBtmBO3Eu7BQ4YWSBFHK08NmV3w9Ukub5jSElTuRKggk8_HJeQ4_aqDrHvygwiEDntMiBQujUTKIapWgnz_WfVLJerPe31_xCgco7wAAdtY'
				// }
			};

			await axios(config)
				.then((response) => {
					// resource = response.data;
					setResource(response.data);
				})
				.catch((error) => {
					console.log(error);
				});
		};

		await getResource();
		setOpen(true);
	}, []);
	// console.log(resource);

	const handleClose = () => {
		setOpen(false);
	};

	console.log(5, document);
	const title = `Care Record Summary (${document.entry[0].resource.id})`;

	return (
		<Card className={clsx(classes.root, className)} {...rest}>
			<CardHeader action={<DocumentMoreButton />} title={title} />
			<Divider />

			<Table>
				<TableBody>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Subject</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								<Link color="inherit" id={document.entry[0].subject.reference} variant="h6" onClick={handleButtonClick}>
									{document.entry[1].resource.name[0].text}
								</Link>
							</Typography>
							{/* <Label color={document.id ? "success" : "error"}>{document.id ? "Success" : "Fail"}</Label> */}
						</TableCell>
					</TableRow>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Encounter Date</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								{moment(document.entry[0].resource.date).format("YYYY-MM-DD HH:MM:SS")}
							</Typography>
						</TableCell>
					</TableRow>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Practitioner</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								{/* <Link color="inherit" id={document.author[0].reference} variant="h6" onClick={handleButtonClick}> */}
								{document.entry[3].resource.name[0].text}
								{/* </Link> */}
							</Typography>
						</TableCell>
					</TableRow>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Department</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								{/* <Link color="inherit" id={document.author[0].reference} variant="h6" onClick={handleButtonClick}> */}
								{document.entry[2].resource.specialty[0].coding[0].display}
								{/* </Link> */}
							</Typography>
						</TableCell>
					</TableRow>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Custodian</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								{/* <Link color="inherit" id={document.custodian.reference} variant="h6" onClick={handleButtonClick}> */}
								{document.entry[4].resource.name}
								{/* </Link> */}
							</Typography>
						</TableCell>
					</TableRow>
					<TableRow>
						<TableCell className={classes.fontWeightMedium}>Encounter Type</TableCell>
						<TableCell>
							<Typography variant="body2" color="textSecondary">
								{document.entry[0].resource.event[0].code[0].coding[0].code}
							</Typography>
						</TableCell>
					</TableRow>
					{sections.map((section) => (
						<TableRow key={section.code.coding[0].code}>
							<TableCell className={classes.fontWeightMedium}>{section.code.coding[0].display}</TableCell>
							<TableCell>
								{section.entry.map((entry) => (
									<Typography key={entry.reference} variant="body2" color="textSecondary">
										<Link color="inherit" id={entry.reference} variant="h6" onClick={handleButtonClick}>
											{entry.reference}
										</Link>
									</Typography>
								))}
							</TableCell>
						</TableRow>
					))}
				</TableBody>
			</Table>
			<Box p={1} display="flex" flexDirection="column" alignItems="flex-start"></Box>
			<Dialog onClose={handleClose} open={open}>
				<DialogTitle>{dialogTitle}</DialogTitle>
				<Divider />
				<DialogContent>
					<ReactJson src={resource} theme="monokai" />
				</DialogContent>
				<Divider />
				<DialogActions>
					<Button onClick={handleClose}>Close</Button>
				</DialogActions>
			</Dialog>
		</Card>
	);
}

DocumentInfo.propTypes = {
	className: PropTypes.string,
	document: PropTypes.object.isRequired,
};

export default DocumentInfo;
