import React, { useState } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { Box, Paper, FormHelperText, Typography, Radio, Button, makeStyles } from "@material-ui/core";

const typeOptions = [
	{
		value: "freelancer",
		title: "I'm a freelancer",
		description: "I'm looking for teamates to join in a personal project",
	},
	{
		value: "projectOwner",
		title: "I’m a project owner",
		description: "I'm looking for freelancer or contractors to take care of my project",
	},
	{
		value: "affiliate",
		title: "I want to join affiliate",
		description: "I'm looking for freelancer or contractors to take care of my project",
	},
];

const useStyles = makeStyles((theme) => ({
	root: {},
	stepButton: {
		"& + &": {
			marginLeft: theme.spacing(2),
		},
	},
}));

function Records({ className, onBack, onNext, ...rest }) {
	const classes = useStyles();
	const [type, setType] = useState(typeOptions[1].value);
	const [isSubmitting, setSubmitting] = useState(false);
	const [error, setError] = useState(null);

	const handleChange = (newType) => {
		setType(newType);
	};

	const handleSubmit = async (event) => {
		event.preventDefault();

		try {
			// Do api call
			setSubmitting(false);

			if (onNext) {
				onNext();
			}
		} catch (err) {
			setError(err.message);
			setSubmitting(false);
		}
	};

	return (
		<form onSubmit={handleSubmit} className={clsx(classes.root, className)} {...rest}>
			<Typography variant="h3" color="textPrimary">
				Please select one option
			</Typography>
			<Box mt={2}>
				<Typography variant="subtitle1" color="textSecondary">
					Proin tincidunt lacus sed ante efficitur efficitur. Quisque aliquam fringilla velit sit amet euismod.
				</Typography>
			</Box>
			<Box mt={2}>
				{typeOptions.map((typeOption) => (
					<Paper display="flex" alignItems="flex-start" p={2} mb={2} component={Box} elevation={type === typeOption.value ? 10 : 1} key={typeOption.value}>
						<Radio checked={type === typeOption.value} onClick={() => handleChange(typeOption.value)} />
						<Box ml={2}>
							<Typography gutterBottom variant="h5" color="textPrimary">
								{typeOption.title}
							</Typography>
							<Typography variant="body1" color="textPrimary">
								{typeOption.description}
							</Typography>
						</Box>
					</Paper>
				))}
			</Box>
			{error && (
				<Box mt={2}>
					<FormHelperText error>{error}</FormHelperText>
				</Box>
			)}
			<Box mt={6} display="flex">
				{onBack && (
					<Button onClick={onBack} size="large">
						Previous
					</Button>
				)}
				<Box flexGrow={1} />
				<Button color="secondary" disabled={isSubmitting} type="submit" variant="contained" size="large">
					Next
				</Button>
			</Box>
		</form>
	);
}

Records.propTypes = {
	className: PropTypes.string,
	onNext: PropTypes.func,
	onBack: PropTypes.func,
};

export default Records;
