import React, { useState } from "react";
import clsx from "clsx";
import PropTypes from "prop-types";
import { Link as RouterLink } from "react-router-dom";
import {
	Avatar,
	Box,
	Breadcrumbs,
	Button,
	Card,
	CardContent,
	Container,
	Grid,
	Link,
	Paper,
	Step,
	StepConnector,
	StepLabel,
	Stepper,
	Typography,
	makeStyles,
	withStyles,
	colors,
} from "@material-ui/core";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import { User as UserIcon, Star as StarIcon, Briefcase as BriefcaseIcon, File as FileIcon } from "react-feather";
import Page from "src/components/Page";
import Records from "./Records";
// import ProjectDetails from "./ProjectDetails";
// import ProjectDescription from "./ProjectDescription";

const steps = [
	{
		label: "User Details",
		icon: UserIcon,
	},
	{
		label: "Project Details",
		icon: BriefcaseIcon,
	},
	{
		label: "Project Description",
		icon: FileIcon,
	},
];

const CustomStepConnector = withStyles((theme) => ({
	vertical: {
		marginLeft: 19,
		padding: 0,
	},
	line: {
		borderColor: theme.palette.divider,
	},
}))(StepConnector);

const useCustomStepIconStyles = makeStyles((theme) => ({
	root: {},
	active: {
		backgroundColor: theme.palette.secondary.main,
		boxShadow: theme.shadows[10],
	},
	completed: {
		backgroundColor: theme.palette.secondary.main,
	},
}));

function CustomStepIcon({ active, completed, icon }) {
	const classes = useCustomStepIconStyles();

	const Icon = steps[icon - 1].icon;

	return (
		<Avatar
			className={clsx(classes.root, {
				[classes.active]: active,
				[classes.completed]: completed,
			})}
		>
			<Icon size="20" />
		</Avatar>
	);
}

CustomStepIcon.propTypes = {
	active: PropTypes.bool,
	completed: PropTypes.bool,
	icon: PropTypes.number,
};

const useStyles = makeStyles((theme) => ({
	root: {
		backgroundColor: theme.palette.background.dark,
		minHeight: "100%",
		paddingTop: theme.spacing(3),
		paddingBottom: theme.spacing(3),
	},
	avatar: {
		backgroundColor: colors.red[600],
	},
}));

function Encounters() {
	const classes = useStyles();
	const [activeStep, setActiveStep] = useState(0);
	const [completed, setCompleted] = useState(false);

	const handleNext = () => {
		setActiveStep((prevActiveStep) => prevActiveStep + 1);
	};

	const handleBack = () => {
		setActiveStep((prevActiveStep) => prevActiveStep - 1);
	};

	const handleComplete = () => {
		setCompleted(true);
	};

	return (
		<Page className={classes.root} title="Project Create">
			<Container maxWidth="lg">
				<Box mb={3}>
					<Breadcrumbs separator={<NavigateNextIcon fontSize="small" />} aria-label="breadcrumb">
						<Link variant="body1" color="inherit" to="/app" component={RouterLink}>
							Dashboard
						</Link>
						<Typography variant="body1" color="textPrimary">
							Projects
						</Typography>
					</Breadcrumbs>
					<Typography variant="h3" color="textPrimary">
						Create Wizard &amp; Process
					</Typography>
				</Box>
				{!completed ? (
					<Paper>
						<Grid container>
							<Grid item xs={12} md={3}>
								<Stepper activeStep={activeStep} connector={<CustomStepConnector />} orientation="vertical" component={Box} bgcolor="transparent">
									{steps.map((step) => (
										<Step key={step.label}>
											<StepLabel StepIconComponent={CustomStepIcon}>{step.label}</StepLabel>
										</Step>
									))}
								</Stepper>
							</Grid>
							<Grid item xs={12} md={9}>
								<Box p={3}>
									{activeStep === 0 && <Records onNext={handleNext} />}
									{/* {activeStep === 1 && <ProjectDetails onBack={handleBack} onNext={handleNext} />}
									{activeStep === 2 && <ProjectDescription onBack={handleBack} onComplete={handleComplete} />} */}
								</Box>
							</Grid>
						</Grid>
					</Paper>
				) : (
					<Card>
						<CardContent>
							<Box maxWidth={450} mx="auto">
								<Box display="flex" justifyContent="center">
									<Avatar className={classes.avatar}>
										<StarIcon />
									</Avatar>
								</Box>
								<Box mt={2}>
									<Typography variant="h3" color="textPrimary" align="center">
										You are all done!
									</Typography>
								</Box>
								<Box mt={2}>
									<Typography variant="subtitle1" color="textSecondary" align="center">
										Donec ut augue sed nisi ullamcorper posuere sit amet eu mauris. Ut eget mauris scelerisque.
									</Typography>
								</Box>
								<Box mt={2} display="flex" justifyContent="center">
									<Button variant="contained" color="secondary" component={RouterLink} to="/app/projects/1">
										View your project
									</Button>
								</Box>
							</Box>
						</CardContent>
					</Card>
				)}
			</Container>
		</Page>
	);
}

export default Encounters;
