import React from "react";
import { useSelector } from "react-redux";
import { Link as RouterLink } from "react-router-dom";
import PropTypes from "prop-types";
import clsx from "clsx";
import Axios from "axios";
// import moment from "moment";
import {
	Avatar,
	Box,
	Button,
	Card,
	CardActions,
	CardMedia,
	Divider,
	// Grid,
	// IconButton,
	Link,
	// SvgIcon,
	// Tooltip,
	Typography,
	colors,
	makeStyles,
} from "@material-ui/core";
// import { Rating } from "@material-ui/lab";
// import FavoriteIcon from "@material-ui/icons/Favorite";
// import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
// import { Users as UsersIcon } from "react-feather";
import GetAppIcon from "@material-ui/icons/GetApp";
import ArchiveIcon from "@material-ui/icons/ArchiveOutlined";
import AccountCircle from "@material-ui/icons/AccountCircle";
import getInitials from "src/utils/getInitials";

const useStyles = makeStyles((theme) => ({
	root: {},
	media: {
		height: 200,
		backgroundColor: theme.palette.background.dark,
	},
	likedButton: {
		color: colors.red[600],
	},
	subscribersIcon: {
		marginLeft: theme.spacing(2),
		marginRight: theme.spacing(1),
	},
	getAppIcon: {
		marignRight: theme.spacing(1),
	},
}));

function OrganizationCard({ organization, className, ...rest }) {
	const classes = useStyles();
	const account = useSelector((state) => state.account);

	// const [isLiked, setLiked] = useState(organization.isLiked);
	// const [likes, setLikes] = useState(organization.likes);

	// const handleLike = () => {
	// 	setLiked(true);
	// 	setLikes((prevLikes) => prevLikes + 1);
	// };

	// const handleUnlike = () => {
	// 	setLiked(false);
	// 	setLikes((prevLikes) => prevLikes - 1);
	// };

	const handleClick = async (event) => {
		console.log(organization, account);
		setLoading(false);

		const fhir_server = organization.fhirURL;
		const organizationID = organization.id;
		const userID = "113610";

		await Axios.get(`${fhir_server}/Composition/$documents/${userID}`).then(async (response) => {
			// console.log(response.data);
			const documents = response.data;
			// setDocuments(documents);
			// console.log(documents);
			for (let i = 0; i < documents.length; i++) {
				// console.log(document.id);
				const phr_server = "http://localhost:3100";

				let data = {
					documentID: documents[i].id,
					userID: userID,
					organizationID: organizationID,
				};

				let phr_config = {
					method: "post",
					url: `${phr_server}/document`,
					data: data,
				};
				// console.log(i);
				await Axios(phr_config)
					.then(async (phr_response) => {
						// console.log(phr_response);
						// console.log(phr_response.data.document.length);
						if (phr_response.data.document.length < 1) {
							const insert_data = {
								documentID: documents[i].id,
								userID: userID,
								organizationID: organizationID,
								document: documents[i],
								isTransfer: false,
							};

							const insert_config = {
								method: "post",
								url: `${phr_server}/documents/insert`,
								data: insert_data,
							};
							// console.log(insert_data, insert_config);

							await Axios(insert_config).then((insert_response) => {
								console.log(insert_response.data);
							});
						}
					})
					.catch((err) => {
						console.log(err);
					});
			}
		});
		setLoading(true);
	};

	return (
		<Card className={clsx(classes.root, className)} {...rest}>
			<Box p={3}>
				<CardMedia className={classes.media} image={organization.media} />
				<Box display="flex" alignItems="center" mt={2}>
					<Avatar alt="Author">{getInitials(organization.title)}</Avatar>
					<Box ml={2}>
						<Link color="textPrimary" component={RouterLink} to="#" variant="h5">
							{organization.title}
						</Link>
						{/* <Typography variant="body2" color="textSecondary">
							by{" "}
							<Link color="textPrimary" component={RouterLink} to="#" variant="h6">
								{organization.author.name}
							</Link>{" "}
							| Updated {moment(organization.updatedAt).fromNow()}
						</Typography> */}
					</Box>
				</Box>
			</Box>
			<Box pb={2} px={3}>
				<Typography color="textSecondary" variant="body2">
					{organization.address}
				</Typography>
				<Typography color="textSecondary" variant="body2">
					{organization.postalCode}
				</Typography>
				<Typography color="textSecondary" variant="body2">
					{organization.telecom}
				</Typography>
			</Box>
			{/* <Box py={2} px={3}>
				<Grid alignItems="center" container justify="space-between" spacing={3}>
					<Grid item>
						<Typography variant="h5" color="textPrimary">
							{organization.location}
						</Typography>
						<Typography variant="body2" color="textSecondary">
							Location
						</Typography>
					</Grid>
					<Grid item>
						<Typography variant="h5" color="textPrimary">
							{organization.type}
						</Typography>
						<Typography variant="body2" color="textSecondary">
							Type
						</Typography>
					</Grid>
					<Grid item>
						<Typography variant="h5" color="textPrimary">
							{organization.technology}
						</Typography>
						<Typography variant="body2" color="textSecondary">
							Technology
						</Typography>
					</Grid>
				</Grid>
			</Box> */}
			<Divider />
			<CardActions>
				<Button
					fullWidth
					href={`${organization.oauth2URL}?client_id=${organization.clientID}&response_type=${organization.responseType}&redirect_uri=${organization.redirectURI}`}
				>
					<AccountCircle className={classes.getAppIcon} />
					Log in
				</Button>
			</CardActions>
			<Divider />
			<CardActions>
				<Button fullWidth onClick={handleClick}>
					<GetAppIcon className={classes.getAppIcon} />
					Download
				</Button>
			</CardActions>
			<Divider />
			<CardActions>
				<Button fullWidth component={RouterLink} to="/phr/records">
					{/* <GetAppIcon className={classes.getAppIcon} /> */}
					<ArchiveIcon className={classes.getAppIcon} />
					Records
				</Button>
			</CardActions>
			<Divider />
			<Box py={2} pl={2} pr={3} display="flex" alignItems="center">
				<Box flexGrow={1} />
				{/* <Button color="secondary" type="submit" variant="contained" size="large">
					Log In
				</Button> */}
				{/* <Button
					color="secondary"
					href={`${organization.oauth2URL}?client_id=${organization.clientID}&response_type=${organization.responseType}&redirect_uri=${organization.redirectURI}`}
					variant="contained"
					size="large"
				>
					LOG IN
				</Button>
				<Button color="secondary" component={RouterLink} to="/phr/records" variant="contained" size="large">
					GET
				</Button>
				<Button color="secondary" component={RouterLink} to="/phr/records" variant="contained" size="large">
					Records
				</Button> */}
				{/* {isLiked ? (
					<Tooltip title="Unlike">
						<IconButton className={classes.likedButton} onClick={handleUnlike}>
							<FavoriteIcon fontSize="small" />
						</IconButton>
					</Tooltip>
				) : (
					<Tooltip title="Like">
						<IconButton onClick={handleLike}>
							<FavoriteBorderIcon fontSize="small" />
						</IconButton>
					</Tooltip>
				)}
				<Typography variant="subtitle2" color="textSecondary">
					{likes}
				</Typography>
				<SvgIcon fontSize="small" color="action" className={classes.subscribersIcon}>
					<UsersIcon />
				</SvgIcon>
				<Typography variant="subtitle2" color="textSecondary">
					{organization.subscribers}
				</Typography>
				<Box flexGrow={1} />
				<Rating value={organization.rating} size="small" readOnly /> */}
			</Box>
		</Card>
	);
}

OrganizationCard.propTypes = {
	className: PropTypes.string,
	organization: PropTypes.object.isRequired,
};

export default OrganizationCard;
