import React, { useState } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { Box, Grid, Typography, makeStyles } from "@material-ui/core";
import { ToggleButtonGroup, ToggleButton } from "@material-ui/lab";
import ViewModuleIcon from "@material-ui/icons/ViewModule";
import OrganizationCard from "src/components/OrganizationCard";

const useStyles = makeStyles((theme) => ({
	root: {},
	title: {
		position: "relative",
		"&:after": {
			position: "absolute",
			bottom: -8,
			left: 0,
			content: '" "',
			height: 3,
			width: 48,
			backgroundColor: theme.palette.primary.main,
		},
	},
	sortButton: {
		textTransform: "none",
		letterSpacing: 0,
		marginRight: theme.spacing(2),
	},
}));

function Results({ className, organizations, handleProgress, ...rest }) {
	const classes = useStyles();
	const [mode, setMode] = useState("grid");

	const handleModeChange = (event, value) => {
		setMode(value);
	};

	return (
		<div className={clsx(classes.root, className)} {...rest}>
			<Box display="flex" alignItems="center" justifyContent="space-between" flexWrap="wrap" mb={2}>
				<Typography className={classes.title} variant="h5" color="textPrimary">
					Showing {organizations.length} organizations
				</Typography>
				<Box display="flex" alignItems="center">
					<ToggleButtonGroup exclusive onChange={handleModeChange} size="small" value={mode}>
						<ToggleButton value="grid">
							<ViewModuleIcon />
						</ToggleButton>
					</ToggleButtonGroup>
				</Box>
			</Box>
			<Grid container spacing={3}>
				{organizations.map((organization) => (
					<Grid item key={organization.id} md={mode === "grid" ? 4 : 12} sm={mode === "grid" ? 6 : 12} xs={12}>
						<OrganizationCard organization={organization} handleProgress={handleProgress} />
					</Grid>
				))}
			</Grid>
		</div>
	);
}

Results.propTypes = {
	className: PropTypes.string,
	organizations: PropTypes.array.isRequired,
};

export default Results;
