import React, { useCallback, useEffect } from "react";
import { Redirect } from "react-router-dom";
import qs from "query-string";
import useIsMountedRef from "src/hooks/useIsMountedRef";
import Axios from "axios";

function OAuth2({ location }) {
	const isMountedRef = useIsMountedRef();
	const organization = localStorage.getItem("organization");

	console.log(JSON.parse(organization));

	let { code } = qs.parse(location.search);
	console.log("OAuth2's code ", code);

	const getToken = useCallback(async () => {
		const data = {
			code: code,
			grant_type: "authorization_code",
			redirect_uri: organization.redirectURI,
		};

		const client = Buffer.from(`${organization.clientID}:${organization.clientSecret}`, "utf8").toString("base64");

		// PME35XannjCftYHt;
		// QJmp8JjylHKGDIIITPv2pkA7kw0ylI4BsQfGGh54AqXh5IpUSOEZo8hEF4JUFT8h;
		// oauth2URL
		// https://localhost:8000/oauth2/authorize
		// console.log(organization.oauth2URL);
		const config = {
			method: "post",
			// url: organization.oauth2URL,
			url: "https://localhost:8000/oauth2/authorize",
			headers: {
				Authorization: `Basic ${client}`,
			},
			data: data,
			withCredentials: true,
		};

		console.log(config);

		await Axios(config)
			.then((response) => {
				console.log(response.data);
			})
			.catch((err) => {
				console.log("error ", err);
			});
	}, [useIsMountedRef]);

	useEffect(() => {
		getToken();
	}, [getToken]);

	// return <Redirect to="/phr" />;
	return <div></div>;
}

export default OAuth2;
