import React, { useCallback, useState, useEffect } from "react";
import { Container, Grid, makeStyles } from "@material-ui/core";
import Page from "src/components/Page";
// import CustomerActivity from "./CustomerActivity";
import EarningsSegmentation from "./EarningsSegmentation";
import FinancialStats from "./FinancialStats";
import Header from "./Header";
import LatestOrders from "./LatestOrders";
// import axios from "src/utils/axios";
import Axios from "axios";
import useIsMountedRef from "src/hooks/useIsMountedRef";
import Overview from "./Overview";
import RoiPerCustomer from "./RoiPerCustomer";

const useStyles = makeStyles((theme) => ({
	root: {
		backgroundColor: theme.palette.background.dark,
		minHeight: "100%",
		paddingTop: theme.spacing(3),
		paddingBottom: theme.spacing(3),
	},
	container: {
		[theme.breakpoints.up("lg")]: {
			paddingLeft: 64,
			paddingRight: 64,
		},
	},
}));

function Records() {
	const classes = useStyles();
	const isMountedRef = useIsMountedRef();
	const [organizations, setOrganizations] = useState([]);
	// const [organization, setOrganization] = useState([]);
	// const userID = '113610';
	const [documents, setDocuments] = useState([]);
	const [documents_1, setDocuments_1] = useState([]);
	const [documents_2, setDocuments_2] = useState([]);
	const [documents_3, setDocuments_3] = useState([]);
	const userID = "113610";

	const getOrganization = useCallback(async () => {
		await Axios.get("http://localhost:3100/organizations").then((response) => {
			if (isMountedRef.current) {
				// console.log(0, response.data.organization);
				setOrganization(response.data.organization);
			}
		});
	}, [isMountedRef]);

	const getDocuments = useCallback(async () => {
		const phr_server = "http://localhost:3100";
		const data = {
			userID: userID,
		};
		const config = {
			method: "post",
			url: `${phr_server}/documents`,
			data: data,
		};

		await Axios(config)
			.then(async (response) => {
				if (isMountedRef.current) {
					// console.log(response.data.documents);
					setDocuments(response.data.documents);
					const results = response.data.documents;
					const results_1 = await results.filter((results) => results.organizationID === "4578");
					console.log(results_1.length, results_1);
					const results_2 = await results.filter((results) => results.organizationID === "4596");
					console.log(results_2.length, results_2);
					const results_3 = await results.filter((results) => results.organizationID === "48651");
					console.log(results_3.length, results_3);
					if (results_1.length > 0) {
						setDocuments_1(results_1);
					}
					if (results_2.length > 0) {
						setDocuments_2(results_2);
					}
					if (results_3.length > 0) {
						setDocuments_3(results_3);
					}
				}
			})
			.catch((err) => {
				console.log(0, err);
			});
	}, [isMountedRef]);

	useEffect(() => {
		getOrganizations();
		getDocuments();
	}, [getOrganizations, getDocuments]);

	console.log(1, documents_1);
	console.log(2, documents_2);
	console.log(3, documents_3);

	return (
		<Page className={classes.root} title="Dashboard Alternative">
			<Container maxWidth={false} className={classes.container}>
				<Header />
				<Grid container spacing={3}>
					{documents_1.length > 0 && (
						<Grid item lg={3} sm={6} xs={12}>
							{/* <Overview name={organization.title} /> */}
							<Overview documents={documents_1} />
						</Grid>
					)}
					{documents_2.length > 0 && (
						<Grid item lg={3} sm={6} xs={12}>
							{/* <Overview name={organization.title} /> */}
							<Overview documents={documents_2} />
						</Grid>
					)}
					{documents_3.length > 0 && (
						<Grid item lg={3} sm={6} xs={12}>
							{/* <Overview name={organization.title} /> */}
							<Overview documents={documents_3} />
						</Grid>
					)}

					<Grid item lg={3} sm={6} xs={12}>
						<RoiPerCustomer />
					</Grid>

					<Grid item lg={8} xl={9} xs={12}>
						<FinancialStats />
					</Grid>
					<Grid item lg={4} xl={3} xs={12}>
						<EarningsSegmentation />
					</Grid>
					<Grid item lg={8} xs={12}>
						<LatestOrders />
					</Grid>
					{/* <Grid item lg={4} xs={12}>
						<CustomerActivity />
					</Grid>
					<Grid item lg={8} xs={12}>
						<MostProfitableProducts />
					</Grid>
					<Grid item lg={4} xs={12}>
						<TopReferrals />
					</Grid> */}
				</Grid>
			</Container>
		</Page>
	);
}

export default Records;
