import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Box, Container, Divider, Tab, Tabs, makeStyles } from "@material-ui/core";
import Page from "src/components/Page";
// import axios from "src/utils/axios";
// import useIsMountedRef from "src/hooks/useIsMountedRef";
import Header from "./Header";
import CategorizedDocuments from "./CategorizedDocuments";
import { Redirect } from "react-router";

const useStyles = makeStyles((theme) => ({
	root: {
		backgroundColor: theme.palette.background.dark,
		minHeight: "100%",
		paddingTop: theme.spacing(3),
		paddingBottom: theme.spacing(3),
	},
}));

function DocumentCategorizationView() {
	const classes = useStyles();
	// const isMountedRef = useIsMountedRef();
	const organizations = useSelector((state) => state.organizations.organizations);
	const documents = useSelector((state) => state.documents.documents);
	// const [customer, setCustomer] = useState(null);
	const [currentTab, setCurrentTab] = useState("total");

	if (!organizations) {
		return <Redirect to="/phr" />;
	}

	// console.log(organizations);
	// const tabs = [
	// 	{ value: "details", label: "Details" },
	// 	{ value: "invoices", label: "Invoices" },
	// 	{ value: "logs", label: "Logs" },
	// ];

	const tabs = [];
	if (organizations) {
		tabs.push({ value: "total", label: "Total" });

		organizations.map((organization) => {
			tabs.push({ value: organization.id, label: organization.title });
		});
	}

	let categorizedDocuments = "total";
	const handleTabsChange = (event, value) => {
		setCurrentTab(value);
		categorizedDocuments = documents.filter((result) => result.organizationID === value);
	};

	// const getCustomer = useCallback(() => {
	// 	axios.get("/api/management/customers/1").then((response) => {
	// 		if (isMountedRef.current) {
	// 			setCustomer(response.data.customer);
	// 		}
	// 	});
	// }, [isMountedRef]);

	// useEffect(() => {
	// 	getCustomer();
	// }, [getCustomer]);

	// if (!customer) {
	// 	return null;
	// }

	return (
		<Page className={classes.root} title="Customer Details">
			<Container maxWidth={false}>
				<Header />
				<Box mt={3}>
					<Tabs onChange={handleTabsChange} scrollButtons="auto" value={currentTab} variant="scrollable" textColor="secondary" className={classes.tabs}>
						{tabs.map((tab) => (
							<Tab key={tab.value} label={tab.label} value={tab.value} />
						))}
					</Tabs>
				</Box>
				<Divider />
				<Box mt={3}>
					<CategorizedDocuments categorizedDocuments={categorizedDocuments} />
					{/* <CategorizedDocuments organizationID={currentTab} /> */}
					{/* {currentTab === "total" && <CategorizedDocuments organizationID={currentTab} />} */}
					{/* {currentTab === "invoices" && <Invoices />}
					{currentTab === "logs" && <Logs />} */}
				</Box>
			</Container>
		</Page>
	);
}

export default DocumentCategorizationView;
